		while (scan.hasNext()) {
			int n = scan.nextInt();
			for (int nl = 0; nl < n; nl++) {
				String s1 = scan.next();
				String s2 = scan.next();
				String aim = "";
				for (int i = 0; i < s1.length(); i++) {
					if (i == s1.length()/2) {
						aim += s2;
					}
					aim += s1.charAt(i);
				}
				System.out.println(aim);
			}
		}
	}
