		while (scan.hasNext()) {
			int n = scan.nextInt();
			for (int nl = 0; nl < n; nl++) {
				String s1 = scan.next();
				String aim = "";
				for (int i = 0; i < s1.length(); i++) {
					aim += s1.charAt(i + 1);
