public class Main {

	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		while (scan.hasNext()) {
			int n = scan.nextInt();
			for (int nl = 0; nl < n; nl++) {
				String s1 = scan.next().toLowerCase();
				String aim="";
				char c=' ';
				for (int i = 0; i < s1.length(); i++) {
					c=s1.charAt(i);
					if(c=='a'||c=='e'||c=='i'||c=='o'||c=='u'){
						c-=32;
					}
					aim+=c;
				}
				System.out.println(aim);
			}
		}
	}
}

