public class Main {

	public static int trans(char c) {
		if (c == 'H' || c == 'h') {
			return 1;
		} else if (c == 'C' || c == 'c') {
			return 12;

		} else if (c == 'N' || c == 'n') {
			return 14;

		} else if (c == 'O' || c == 'o') {
			return 16;

		} else if (c == 'F' || c == 'f') {
			return 19;

		} else if (c == 'P' || c == 'p') {
			return 31;

		} else if (c == 'S' || c == 's') {
			return 32;

		} else if (c == 'K' || c == 'k') {
			return 39;
		}
		return 0;
	}

	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		while (scan.hasNext()) {
			int count = scan.nextInt();
			for (int tls = 0; tls < count; tls++) {
				String str = scan.next();
				int sum = 0;
				for (int i = 0; i < str.length(); i++) {
					char t = str.charAt(i);
					if (Character.isLetter(t)) {
						sum += trans(t);
						continue;
					}
					if (Character.isDigit(t)) {
						int start = i;
						while (i < str.length()&&Character.isDigit(str.charAt(i))) {
							i++;
						}
						int num = Integer.valueOf(str.substring(start, i ));
						sum += (num - 1) * trans(str.charAt(start - 1));
						i--;
					}
				}
				System.out.println(sum);
			}
		}
	}
}

import java.util.Scanner;

/**
 * @Title_Test.java
 * @author_Stone6762
 */
public class Main {

	public static int trans(char c) {
		if (c == 'A') {
			return 4;
		} else if (c == 'B') {
			return 3;
		} else if (c == 'C') {
			return 2;
		} else if (c == 'D') {
			return 1;
		} else if (c == 'F') {
			return 0;
		} else {
			return -1;
		}

	}

	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		while (scan.hasNext()) {
			String str=scan.nextLine().trim();
			String arr[]=str.split(" +");
			double sum=0;
			boolean flag=true;
			for (int i = 0; i < arr.length; i++) {
				int t=trans(arr[i].charAt(0));
				if(t>=0){
					sum+=t;
				}else{
					flag=false;
					break;
				}
			}
			if(flag){
				System.out.printf("%.2f\n",sum/arr.length);
			}else{
				System.out.println("Unknown");
			}
		}
	}
}
